import React from 'react';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';

import {setDialog as productDialog} from '../../../actions/productActions'
import {setCart} from '../../../actions/cartAction'
import {setSnackbar} from '../../../actions/appActions'
import { checkImageUrl } from '../../../providers/commonMethods'
import { StyledAppBar } from '../../Layout/Header/style'

import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import Snackbar from '@material-ui/core/Snackbar';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
//
import Checkbox from '@material-ui/core/Checkbox';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import FormHelperText from '@material-ui/core/FormHelperText';


import { FormatAlignLeftSharp } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop:64,
  },
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
  media: {
    height: 350,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function FullScreenProductDialog(props) {
  const classes = useStyles();
  //let [open, setOpen] = React.useState(false);
  const handleClose = () => {
    props.productDialog(false);
  };
  const closeSnack = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    props.setSnackbar({open: false, message: ""});
  };
    const { item, cart, pDialog } = props;
    const addCart = () => {
      props.setSnackbar({open:true, message:"Adicionado com sucesso!"})
      if(JSON.parse(localStorage.getItem("cart")) === null){
      //if(cart.lenght == 0){
        let cartItems = [item];
        localStorage.setItem("cart", JSON.stringify(cartItems));
        props.setCart(cartItems)
      }else{
        let cartItems = JSON.parse(localStorage.getItem("cart"));
        cartItems.push(item);
        localStorage.setItem("cart", JSON.stringify(cartItems));
        props.setCart(cartItems)
      }

    }
    let hostUrl = "http://services.yamitec.com";
  return (
    <div>
      <Dialog fullScreen open={pDialog} onClose={() => handleClose()} TransitionComponent={Transition}>
        <StyledAppBar position="fixed">
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={() => handleClose()} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              {item ===undefined ? ("") : (item.name)}
            </Typography>
          </Toolbar>
        </StyledAppBar>
        { item === undefined ? (<p>Nada selecionado</p>) : (
        <Card className={classes.root}>
          <CardActionArea>
            <CardMedia
              className={classes.media}
              image={checkImageUrl(item.thumb) ? item.thumb : checkImageUrl(hostUrl+"/"+item.thumb) ? hostUrl+"/"+item.thumb : hostUrl+"/public/assets/images/favicon.png"}
              title={item.name}
            />
            <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                {item.name}
                </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
              {item.description}
              </Typography>
              <Typography gutterBottom variant="h5" component="h2">
                R$ {item.price.toLocaleString("pt-BR")}
                </Typography>
                <div style={{flex: 2, flexDirection: 'column'}}>
                  {item.attr.map(attr => {
                    if(attr.type === "check"){
                      return (
                      <div>
                        <FormControl component="fieldset" className={classes.formControl}>
                        <FormLabel component="legend">{attr.name}</FormLabel>
                        <FormGroup>
                          <FormControlLabel
                            control={<Checkbox checked={true} onChange={() => {}} name={attr.name} />}
                            label={attr.values[0]}
                          />
                          </FormGroup>
                          <FormHelperText></FormHelperText>
                        </FormControl></div>)
                    }else{
                      return (<div>
                        <FormControl component="fieldset">
                        <FormLabel component="legend">{attr.name}</FormLabel>
                        <RadioGroup aria-label={attr.name} name={attr.name}>
                        {attr.values.map(value => {
                          <FormControlLabel value={value} control={<Radio />} label={value} />
                        })}
                        </RadioGroup>
                      </FormControl>
                      </div>)
                    }

                  })}
                </div>
            </CardContent>
          </CardActionArea>
          <CardActions style={{position: 'fixed', bottom: 0,}}>
            <Button fullWidth size="big" variant="contained" color="primary" onClick={() => addCart()}
            style={{
              position: 'fixed',
              bottom: 0, left: 0,
              background: 'linear-gradient(45deg, #025ea2 30%, #0086e8 90%)',
            }}>
              Adicionar
            </Button>
          </CardActions>
        </Card>
        )}
        <Snackbar
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        open={props.snackbar.open}
        onClose={closeSnack}
        autoHideDuration={3000}
        message={props.snackbar.message}
      />
      </Dialog>
    </div>
  );
}
const mapStateToProps = store => ({
  snackbar: store.appReducer.snackbar,
  pDialog: store.productReducer.productDialog,
  cart: store.cartsReducer.cart
});
const mapDispatchToProps = dispatch =>
bindActionCreators({ setSnackbar, productDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(FullScreenProductDialog)
