import React, { Component, Fragment, useState,  useRef } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
//
import { DataGrid, RowsProp, ColDef } from '@material-ui/data-grid';
//
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
import { DEFAULT_LOCALE_TEXT } from '../../providers/langs/datagrid'
//const classes = useStyles();
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
//
import TextField from '@material-ui/core/TextField';
import Input from '@material-ui/core/Input';

import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import ptBR from "date-fns/locale/pt-BR";
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';

import PropTypes from 'prop-types';
import MaskedInput from 'react-text-mask';
//

//const [valus, setValues] = useState(new Date('2021-02-13'));
const idNumbers = [
    'cpf', 'cnpj'
]
let inputValues = {};
function onChangeInputs(e) {
    if(e.target.value.length == 0 || e.target.value.length == 'Todos'){
        delete inputValues[e.target.id ?? e.target.name];
    }else{
        let value = e.target.value;
        if(e.target.id == 'created_at'){
            value =  `${value.toJSON().split('T')[0]}`
        }
        if(idNumbers.includes(e.target.id)){
            value = value.replace(/[^\d]+/g,'');
        }
        inputValues[e.target.id ?? e.target.name] = value
        console.log(inputValues)
    }
}
// MASKED INPUTS 

const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
      },
    },
  }));

  function TextMaskCustom(props) {
    const { inputRef, ...other } = props;
  
    return (
      <MaskedInput
        {...other}
        ref={(ref) => {
          inputRef(ref ? ref.inputElement : null);
        }}
        mask={props.mask}
        onFocus={(ref) => {
            inputRef.current.selectionStart = 0;
        }}
        placeholderChar={'\u2000'}
        showMask
      />
    );
  }


//
const DateInput = (props) => {
    const [value, setValue] = useState(new Date('2021-02-13'));
    function handleChange(value) {
        try{
            inputValues[props.id] =  `${value.toJSON().split('T')[0]}`
        }catch(err){
            console.log(err);
        }
        setValue(value);
    }
    return (<MuiPickersUtilsProvider utils={DateFnsUtils} locale={ptBR}>
                <Grid justify="space-around" style={{marginTop: '25px'}}>
                <KeyboardDatePicker
                    margin="normal"
                    id={props.id}
                    label={props.label ?? 'Data'}
                    format="dd/MM/yyyy"
                    value={value}
                    onChange={handleChange}
                    onBlur={handleChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                    />
                </Grid>
            </MuiPickersUtilsProvider>)
}
//

const SelectInput = (props) => {
    const [value, setValue] = useState(props.values[0]);
    function handleChange(e) {
        onChangeInputs(e)
        setValue(e.target.value);
    }
    return (
        <FormControl id={props.column} style={{marginTop: '25px'}}>
                <InputLabel id={props.column}>{props.label}</InputLabel>
                <Select
                    labelId={props.id}
                    id={props.id}
                    name={props.name}
                    value={value}
                    onChange={handleChange}
                    onBlur={handleChange}
                >
                    {
                        props.values.map(val => {
                            return <MenuItem  value={val}>{val}</MenuItem>
                        })
                    }
                    
                </Select>
        </FormControl>)
}
class LDataGrid extends Component {
    state = {
        data: [],
    }
    async setPage(params = { page: 1 }) {
        const data = await this.props.pageRequest(params);
        this.setState({ ...this.state, data })
    }
    async componentDidMount() {
        this.setPage();
    }

    render() {
        const classes = {
            m5: {
                margin: 5,
                marginTop: 25,
                
            },
            root: {
                maxWidth: 345,
                marginBottom: 25
            },
            media: {
                height: 140,
            },
        }

        return (
            <div>
                <Card className={classes.root}>
                    <CardContent style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(4, 1fr)',
                        gridColumnGap: '10px',
                        gridRowGap: '10px'
                    }}>
                        {
                            this.props.filterInputs.map(input => {
                                if(input.type == "text") {
                                    if(input.mask === undefined)
                                        return <TextField style={{...classes.m5, width: input.width ?? 200}} id={input.column} label={input.label} onChange={onChangeInputs} onBlur={onChangeInputs} />
                                    else
                                        return (
                                            <FormControl style={{...classes.m5, width: input.width ?? 100}} >
                                                <InputLabel htmlFor="formatted-text-mask-input">{input.label}</InputLabel>
                                                <Input
                                                
                                                value={inputValues[input.column]}
                                                onChange={onChangeInputs}
                                                name={input.column}
                                                id={input.column}
                                                inputProps={{
                                                    mask: input.mask
                                                  }}
                                                inputComponent={TextMaskCustom}
                                                />
                                            </FormControl>)

                                }else if(input.type == "date") {
                                    return <DateInput id={input.column} style={{...classes.m5, width: input.width ?? 200}} onBlur={onChangeInputs} />
                                }else if(input.type == "select"){
                                    return ( <SelectInput id={input.column} label={input.label} name={input.column} values={input.values} style={{...classes.m5, width: input.width ?? 200 }} onBlur={onChangeInputs} />)
                                }
                            })
                        }
                    <Button onClick={() => {this.setPage(inputValues)}}> Buscar </Button>
                    </CardContent>
                    <CardActionArea>
                    </CardActionArea>
                    <CardActions>

                    </CardActions>
                </Card>

                <div style={{ height: window.innerHeight - 250, width: '100%' }}>
                    <DataGrid rows={this.state.data.data ?? []} columns={this.props.columns}
                        disableClickEventBubbling
                        localeText={DEFAULT_LOCALE_TEXT}
                        paginationMode="server"
                        rowCount={this.state.data.total ?? 0}
                        pageSize={10} rowsPerPageOptions={[10, 20, 50]} pagination
                        onPageChange={(params) => {
                            console.log(params);
                            this.setPage({ page: params.page, pageSize: params.pageSize });
                        }}
                    />
                </div>
            </div>
        )
    }
}
const mapStateToProps = store => ({

});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(LDataGrid)
