import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
//
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import DataGrid from '../../components/List/datagrid';
//
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
import {getApiContributors} from '../../providers/api'
//const classes = useStyles();
class Contributors extends Component {
    state = {
        contributors: [],
    }
    /*async setPage(params = {page: 1} ){
        const contributors = await getApiContributors(params);
        this.setState({...this.state, contributors }) 
    } */
    async componentDidMount() {
      //  this.setPage();
    }

    render() {
        const authData = JSON.parse(localStorage.getItem("user"));
        const classes = {
            root: {
                maxWidth: 345,
            },
            media: {
                height: 140,
            },
        }
        const rows = this.state.contributors.data ?? [];
        const columns = [
            { field: 'id', headerName: 'Id', width: 100 },
            { field: 'name', headerName: 'Nome', width: 150 },
            { field: 'function', headerName: 'Função', width: 150 },
            { field: 'username', headerName: 'Usuário', width: 150 },
            { 
                field: 'active', 
                headerName: 'Ativo', 
                width: 100,
                valueFormatter: (params: ValueFormatterParams) =>
                {
                    return params.value === 1 ? "Ativo" : "Inativo"
                }
            },
        ];

        return (
            <Fragment>
                 <Typography variant="h4">
                        Colaboradores
                    </Typography>
                <Card className={classes.root}>
                    <CardContent>
                        <DataGrid rows={rows} columns={columns} pageRequest={getApiContributors} />
                    </CardContent>
                    <CardActionArea>
                    </CardActionArea>
                    <CardActions>

                    </CardActions>
                </Card>
            </Fragment>
        )
    }
}
const mapStateToProps = store => ({
    session: store.authReducer.data,
    products: store.productReducer.products
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Contributors)
