import React, { Component, Fragment } from 'react'
import {ScrollView, Text} from 'react-native';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import { setDialog, setSearch } from '../../actions/searchActions'
import { getQuery, getWiki } from '../../providers/api'
import { setSnackbar } from '../../actions/appActions'
//*** */
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import ImageIcon from '@material-ui/icons/Image';
import WorkIcon from '@material-ui/icons/Work';
import BeachAccessIcon from '@material-ui/icons/BeachAccess';

import mongoose from  'mongoose';
/** Assets */
import { Box, CardMedia, Paper, TextField } from '@material-ui/core';

//const classes = useStyles();
class Searcher extends Component {
    state = {
        query: '',
        items: [],
        wiki: {}
    }
    
    componentDidMount() {

    }

    dialogHandler(item) {
        this.props.setDialog(true)
        this.setState({ ...this.state, item });
    }
    async handleKeyDown(e) {
        if(e.nativeEvent.key == "Enter"){
            const value = e.target.value;
            if(value === ''){
              this.setState({ ...this.state, query: '' ,items: [], wiki: undefined });
              return false;
            }
            const items = await getQuery(value);
            const wiki = await getWiki(value);
            this.setState({ ...this.state, items, wiki });
            ///alert(e.target.value);
            console.log(this.state);
        }
    }
    onChange = (e) => {        
        this.setState({...this.state, query: e.target.value});
    }

    render() {
      const classes = {
        root: {
          width: '55%',
          maxWidth: '26ch',
        },
        papers: {
          display: 'flex',
          flexDirection: 'column',
          flexWrap: 'wrap',
          '& > *': {
            padding: 5,
            margin: 1,
            width: 16,
          },
        },
        inline: {
          display: 'inline',
        },
      }
        return (
            <Box display="flex">
              <Paper style={{width: (this.state.wiki.info === undefined || Object.keys(this.state.wiki.info).length == 0) ? '95vw' :'67vw', margin: '0.8vw'}}>
                 <TextField fullWidth id="outlined-basic" label="Digite um termo para a sua busca" variant="outlined" 
                 onKeyUp={(e) => this.handleKeyDown(e)} 
                 onChange={(e) => this.onChange(e)} />
                 {this.state.items.length > 0 ? (
                  <ScrollView style={{height: 450, padding: 5}}>
                    <small>Total : {this.state.items.length} itens encontrados.</small>
                    {this.state.items.map((v,k) =>{return (
                    <List className={classes.root}>
                      <ListItem>
                        <ListItemAvatar>
                          <Avatar src={v.favicon}></Avatar>
                        </ListItemAvatar>
                    <ListItemText primary={<a style={{textDecoration: 'none', color: '#025ea2'}} href="">{v.title}</a>} secondary={<div><span style={{
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                      display: '-webkit-box',
                      webkitLineClamp: 2,
                      webkitBoxOrient: 'vertical'
                    }}>{v.description}</span><br /><small>{v.uri}</small></div>}/>
                      </ListItem>
                    </List>
                    )})}
                  </ScrollView>) : ('')}
                  </Paper>
                  { this.state.wiki.info !== undefined && Object.keys(this.state.wiki.info).length > 0 ? (
                  <Paper style={{width: '24vw',margin: '0.5vw', padding: 15}}>
                    {this.state.wiki.image ? (<CardMedia
                      image={this.state.wiki.image}
                      style={{height: 145, margin:-15}}
                      title={this.state.wiki.name}
                    />) : ('')}
                    <h1> {this.state.wiki.info ? this.state.wiki.info.name : '' } </h1>
                    <Text> {this.state.wiki.summary ? this.state.wiki.summary.slice(0,400)+'...' : ''} </Text>
                    <p><a style={{textDecoration: 'none', color: '#025ea2'}} href={this.state.wiki.url ?? ''}>Ler mais</a></p>
                  </Paper>): ('')}
            </Box>
        )
    }
}
const mapStateToProps = store => ({
    dialog: store.searchReducer.searchDialog,
    search: store.searchReducer.search
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setSearch, setSnackbar, setDialog }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Searcher);
