import React , { useState, useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native'
import { makeStyles } from '@material-ui/core/styles';
import Timeline from '@material-ui/lab/Timeline';
import TimelineItem from '@material-ui/lab/TimelineItem';
import TimelineSeparator from '@material-ui/lab/TimelineSeparator';
import TimelineConnector from '@material-ui/lab/TimelineConnector';
import TimelineContent from '@material-ui/lab/TimelineContent';
import TimelineOppositeContent from '@material-ui/lab/TimelineOppositeContent';
import TimelineDot from '@material-ui/lab/TimelineDot';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

//ICONS
import FastfoodIcon from '@material-ui/icons/Fastfood';
import CheckIcon from '@material-ui/icons/Check';
import MotorcycleIcon from '@material-ui/icons/Motorcycle';
import SportsMotorsportsIcon from '@material-ui/icons/SportsMotorsports';

import AccessTimeIcon from '@material-ui/icons/AccessTime';
import WhatshotIcon from '@material-ui/icons/Whatshot';

import LaptopMacIcon from '@material-ui/icons/LaptopMac';
import HotelIcon from '@material-ui/icons/Hotel';
import RepeatIcon from '@material-ui/icons/Repeat';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Snackbar from '@material-ui/core/Snackbar';
///
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import CardDialogLoading from '../../Loading/CardDialogLoading'
import { StyledAppBar } from '../Header/style'
import Dialog from '@material-ui/core/Dialog';
import { setSnackbar } from '../../../actions/appActions'
import { setDialog, setTimeline } from '../../../actions/timelineAction'

import { checkImageUrl } from '../../../providers/commonMethods'
import Toolbar from '@material-ui/core/Toolbar';
import Slide from '@material-ui/core/Slide';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: 84,
  },
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
  media: {
    height: 140,
  },
  paper: {
    padding: '6px 16px',
  },
  secondaryTail: {
    backgroundColor: theme.palette.secondary.main,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function FullScreenTimelineDialog(props) {
  const classes = useStyles();
  const handleClose = () => {
    props.setDialog(false);
  };

  const { timeline, cDialog, setTimeline } = props;

  const [remove, setRemove] = useState(true);

  const closeSnack = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    props.setSnackbar({ open: false, message: "" });
  };

  const doEffect = () => {
    setTimeline({
      id: 1,
      name: 'Produto de Teste',
      description: 'Descrição de Produto de Teste',
      price: 19.99,
      status: [
        {
          id: 1,
          icon: <FastfoodIcon />,
          title: 'Pedido Realizado',
          description: 'Aguardando aprovação do Restaurante',
          timestamp: '9:22 AM'
        },{
          id: 2,
          icon: <AccessTimeIcon />,
          title: 'Pedido Aceito',
          description: 'Aguardando o restaurante iniciar o preparo',
          timestamp: '9:32 AM'
        },{
          id: 3,
          icon: <WhatshotIcon />,
          title: 'Preparando Pedido',
          description: 'O restaurante está preparando o pedido',
          timestamp: '9:42 AM'
        },{
          id: 4,
          icon: <AccessTimeIcon />,
          title: 'Pedido Pronto',
          description: 'O Seu Pedido está pronto e aguardando para a entrega',
          timestamp: '9:47 AM'
        },{
          id: 5,
          icon: <SportsMotorsportsIcon />,
          title: 'Aguardando Entregador',
          description: 'O seu pedido está pronto para a entrega, e aguardando o entregador.',
          timestamp: '9:50 AM'
        },{
          id: 6,
          icon: <MotorcycleIcon />,
          title: 'A Caminho',
          description: 'O Seu pedido está a caminho',
          timestamp: '9:52 AM'
        },{
          id: 7,
          icon: <CheckIcon />,
          title: 'Pedido Entregue',
          description: 'O Seu Pedido foi entregue. Faça uma breve Avaliação',
          timestamp: '10:15 AM'
        }
      ]
    });
    /*if (timeline.length === 0 && JSON.parse(localStorage.getItem("cart")) !== null) {
      let cartItems = JSON.parse(localStorage.getItem("cart"));
      //cartItems.push(item);
      //localStorage.setItem("cart", JSON.stringify(cartItems));
      props.setCart(cartItems)
    }*/
    console.log(timeline)
  }
  useEffect(() => {
    if(timeline === undefined)
      doEffect()
  }, [timeline]);
  //localStorage.removeItem("cart")
  console.log(timeline);
  return (
    <div>
      <Dialog fullScreen open={cDialog} onClose={() => handleClose()} TransitionComponent={Transition}>
        <StyledAppBar position="fixed">
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={() => handleClose()} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Detalhes do Pedido {timeline!== undefined ? "#"+timeline.id : '' }
            </Typography>
          </Toolbar>
        </StyledAppBar>
        {timeline === undefined ?(<Text style={{marginTop: 84}} >Indisponivel</Text>) : (
        <Timeline style={{marginTop: 84}} align="alternate">
          {timeline.status.map((v, k) => {
            return (
          <TimelineItem key={k} >
            <TimelineOppositeContent>
              <Typography variant="body2" color="textSecondary">
                {v.timestamp}
              </Typography>
            </TimelineOppositeContent>
            <TimelineSeparator>
            {
              k < 5 ?
              (<TimelineDot color="primary">{v.icon}</TimelineDot>)
              :
              (<TimelineDot>{v.icon}</TimelineDot>)
            }
            {
              (timeline.status.length-2) >= k ?
              (<TimelineConnector className={ k > 3 ? '' : classes.secondaryTail} />
              )
              : ("")
            }

            </TimelineSeparator>
            <TimelineContent>
              <Paper elevation={3} className={classes.paper}>
                <Typography variant="h6" component="h1">
                {v.title}
                </Typography>
                <Typography>{v.description}a</Typography>
              </Paper>
            </TimelineContent>
          </TimelineItem>
          )})}
        </Timeline>)}
    <Snackbar
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
          open={props.snackbar.open}
          onClose={closeSnack}
          autoHideDuration={3000}
          message={props.snackbar.message}
        />
      </Dialog>
    </div>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    paddingHorizontal: 10
  },
  button: {
    alignItems: "center",
    //backgroundColor: "#DDDDDD",
    padding: 10
  },
  countContainer: {
    alignItems: "center",
    padding: 10
  }
});
const mapStateToProps = store => ({
  snackbar: store.appReducer.snackbar,
  cDialog: store.timelineReducer.timelineDialog,
  timeline: store.timelineReducer.timeline
});
const mapDispatchToProps = dispatch =>
  bindActionCreators({ setSnackbar, setDialog, setTimeline }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(FullScreenTimelineDialog)
