import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
//
import { DataGrid, RowsProp, ColDef } from '@material-ui/data-grid';
//
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
import { getApiContributors } from '../../providers/api'
import { DEFAULT_LOCALE_TEXT } from '../../providers/langs/datagrid'
import { browserHistory } from "react-router-dom"
//const classes = useStyles();
class LDataGrid extends Component {
    state = {
        contributors: [],
    }
    async setPage(params = { page: 1 }) {
        const contributors = await this.props.pageRequest(params);
        this.setState({ ...this.state, contributors })
    }
    async componentDidMount() {
        this.setPage();
    }

    render() {
        let columnDefault = {
            field: 'id', headerName: 'Id', width: 100
        } 

        const rows = this.state.contributors.data ?? [];
        const columns = [
            { field: 'id', headerName: 'Id', width: 150 },
            { field: 'name', headerName: 'Nome', width: 150 },
            { field: 'function', headerName: 'Função', width: 150 },
            { field: 'username', headerName: 'Usuário', width: 150 },
            {
                field: 'active',
                headerName: 'Ativo',
                width: 100,
                valueFormatter: (params: ValueFormatterParams) => {
                    return params.value === 1 ? "Ativo" : "Inativo"
                }
            },
        ];

        return (
            <div style={{ height: 300, width: '100%' }}>
                <DataGrid rows={this.state.contributors.data ?? []} columns={columns}
                    disableClickEventBubbling
                    localeText={DEFAULT_LOCALE_TEXT}
                    paginationMode="server"
                    rowCount={this.state.contributors.total ?? 0}
                    pageSize={10} rowsPerPageOptions={[10, 20, 50]} pagination
                    onPageChange={(params) => {
                        this.setPage({ page: params.page });
                    }}
                />
            </div>
        )
    }
}
const mapStateToProps = store => ({
    session: store.authReducer.data,
    products: store.productReducer.products
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(LDataGrid)
