import React, { Component, Fragment, useState, useRef } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import { withRouter } from 'react-router-dom'
//
//
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
//const classes = useStyles();
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import Snackbar from '@material-ui/core/Snackbar';
//
import TextField from '@material-ui/core/TextField';
import Input from '@material-ui/core/Input';

import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import IndeterminateCheckBoxIcon from '@material-ui/icons/IndeterminateCheckBox';

import ptBR from "date-fns/locale/pt-BR";
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker,
} from '@material-ui/pickers';

import MaskedInput from 'react-text-mask';
const idNumbers = [
    'cpf', 'cnpj', 'cep'
];

let inputValues = {};
let formValidate = {};
function onChangeInputs(e, params = {}) {
    let value = e.target.value;
    let id = e.target.id ?? e.target.name;

    if (idNumbers.includes(e.target.id)) {
        value = value.replace(/[^\d]+/g, '');
    }

    if (value.length == 0 || value == 'Todos') {
        inputValues[id] = '';
    } else {
        if (id == 'created_at') {
            value = `${value.toJSON().split('T')[0]}`
        }

        if (idNumbers.includes(id)) {
            value = value.replace(/[^\d]+/g, '');
        }
        if (params.handle !== undefined) {
            //let request = await params.handle(value)
            //value = request[id] ?? '';
        }


        if (params.json === undefined) {
            inputValues[id] = value
            //console.log(params.json);
        } else {
            inputValues[params.json][id] = value;
        }
    }
    formValidate[id] = value;
    //console.log(inputValues);
    return true;
}

// MASKED INPUTS 

function TextMaskCustom(props) {
    const { inputRef, ...other } = props;

    return (
        <MaskedInput
            {...other}
            ref={(ref) => {
                inputRef(ref ? ref.inputElement : null);
            }}
            value={props.value}
            onChange={props.onChange}
            onBlur={props.onChange}
            mask={props.mask}
            placeholderChar={'\u2000'}

        />
    );
}

//
function TextInputCustom(props) {
    const { inputRef, ...other } = props;

    const [value, setValue] = useState("");
    const [error, setError] = useState(false);

    function handleChange(e) {
        const { id } = e.target;
        let val = e.target.value;
        setValue(val);
        if (props.validate !== undefined) {
            if (props.validate(val) !== false) {
                setError(false);
            } else {
                setError(true)
            }
        }
        props.onChange(e)
    }

    if (props.mask === undefined)
        return (
            <TextField key={`input-${props.id}`} size="small" style={props.style}
                required={props.required ?? false}
                error={error}
                value={value}
                helperText={error ? props.helperText ?? "conteúdo inválido" : ""}
                id={props.id} label={props.label}
                onChange={handleChange}
                onBlur={handleChange}
            />
        )
    else
        return (
            <FormControl key={`input-${props.id}`} style={props.style} >
                <InputLabel htmlFor={props.id}>{props.label}</InputLabel>
                <Input
                    required={props.required ?? false}
                    size="small"
                    key={`input-${props.id}`}
                    error={error}
                    onChange={handleChange}
                    onBlur={handleChange}
                    name={props.name}
                    id={props.id}
                    aria-describedby="component-error-text"
                    inputProps={{
                        mask: props.mask,
                        error,
                    }}
                    inputComponent={TextMaskCustom}
                />
                {error == true ? (
                    <FormHelperText id="component-error-text">{props.helperText ?? "conteúdo inválido"}</FormHelperText>
                ) : ('')}
            </FormControl>)
}
//
const DateInput = (props) => {
    const [value, setValue] = useState(new Date('2021-02-13'));
    const [error, setError] = useState(false);
    function handleChange(value) {
        try {
            if (props.validate !== undefined) {
                if (props.validate(value)) {
                    setError(false);
                    inputValues[props.id] = `${value.toJSON().split('T')[0]}`
                } else {
                    setError(true)
                }
            }

        } catch (err) {
            console.log(err);
        }
        //props.onChange(value);
        setValue(value);
    }
    return (<MuiPickersUtilsProvider utils={DateFnsUtils} locale={ptBR}>
        <Grid justify="space-around" style={{ flexGrow: 0, marginTop: 18 }}>
            <KeyboardDatePicker
                required={props.required ?? false}
                margin="normal"
                id={props.id}
                label={props.label ?? 'Data'}
                error={error}
                helperText={props.error ? props.helperText ?? "conteúdo inválido" : ""}
                format="dd/MM/yyyy"
                value={value}
                onChange={handleChange}
                onBlur={handleChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
            />
        </Grid>
    </MuiPickersUtilsProvider>)
}
//

const SelectInput = (props) => {
    const [value, setValue] = useState(props.values[0]);
    const [error, setError] = useState(false);
    function handleChange(e) {
        const { value, id } = e.target;
        if (props.validate !== undefined) {
            if (props.validate(value)) {
                setError(false);
                props.onChange(e);
            } else {
                setError(true)
            }
        }
        setValue(e.target.value);
    }
    return (
        <FormControl id={props.column} style={{ ...props.style, marginTop: '25px' }}>
            <InputLabel id={props.column}>{props.label}</InputLabel>
            <Select size="small"
                labelId={props.id}
                id={props.id}
                name={props.name}
                value={value}
                error={error}
                helperText={props.error ? props.helperText ?? "conteúdo inválido" : ""}
                onChange={handleChange}
                onBlur={handleChange}
            >
                {
                    props.values.map((val, ind) => {
                        return <MenuItem key={`input-${ind}`} value={val}>{val}</MenuItem>
                    })
                }

            </Select>
        </FormControl>)
}
class LForms extends Component {
    state = {
        data: [],
        inputVal: {},
        filter: ['flex']
    }
    async componentDidMount() {
        let inputValues = {};
        let formValidate = {};
        this.props.forms.map((form, ind) => {
            let ifjson = {};
            form.fields.map((input, ind1) => {
                ifjson[input.column] = input.value ?? '';
                inputValues[form.json ?? input.column] = form.json == undefined ? input.value ?? '' : ifjson;
                formValidate[input.column] = input.value ?? '';
            });
        });
        this.setState({ ...this.state, inputVal: inputValues, formValidate  });

        console.log(this.state)
    }


    render() {
        const mainChange = (e, params) => {
            let inputValues = this.state.inputVal;
            let formValidate = this.state.formValidate;
            let value = e.target.value;
            let id = e.target.id ?? e.target.name;

            if (idNumbers.includes(e.target.id)) {
                value = value.replace(/[^\d]+/g, '');
            }

            if (value.length == 0 || value == 'Todos') {
                inputValues[id] = '';
            } else {
                if (id == 'created_at') {
                    value = `${value.toJSON().split('T')[0]}`
                }

                if (idNumbers.includes(id)) {
                    value = value.replace(/[^\d]+/g, '');
                }
                if (params.handle !== undefined) {
                    //let request = await params.handle(value)
                    //value = request[id] ?? '';
                }


                if (params.json === undefined) {
                    inputValues[id] = value
                    //console.log(params.json);
                } else {
                    inputValues[params.json][id] = value;
                }
            }
            formValidate[id] = value;
            this.setState({ ...this.state, inputVal: inputValues, formValidate  });
            console.log(this.state.inputVal);
        }

        const classes = {
            m5: {
                margin: 5,
                marginTop: 25,
                //flexBasis: '30%'

            },
            root: {
                maxWidth: 345,
                marginBottom: 25
            },
            media: {
                height: 140,
            },
        }
        return (
            <div>
                {
                    this.props.forms.map((form, ind) => {
                        return (
                            <div>
                                <Card className={classes.root} style={{ marginBottom: 15 }}>
                                    <CardContent>
                                        <Typography onClick={() => {
                                            let filter = this.state.filter;
                                            filter[ind] = this.state.filter[ind] == 'flex' ? 'none' : 'flex'
                                            this.setState({ ...this.state, filter })
                                        }}>
                                            <IndeterminateCheckBoxIcon /> {form.title}
                                        </Typography>

                                        <div key={`block-${ind}`} id={`data-form-${form.id ?? '1'}`} style={{
                                            alignItems: 'center',
                                            flexFlow: form.flexFlow ?? 'row wrap',
                                            justifyContent: 'start',
                                            display: this.state.filter[ind] ?? 'flex',
                                        }}>
                                            {

                                                form.fields.map((input, ind1) => {
                                                    //}
                                                    if (input.type == "text") {
                                                        return <TextInputCustom key={`input-${ind1}`} id={input.column} style={{ ...classes.m5, flexGrow: input.grow ?? 0, flexBasis: input.flexBasis ?? '30%' }}
                                                            id={input.column} label={input.label}
                                                            mask={input.mask ?? undefined}
                                                            validate={input.validateHandler}
                                                            helperText={input.helperText ?? ""}
                                                            onChange={(e) => mainChange(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })}
                                                            onBlur={(e) => mainChange(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })} />

                                                    } else if (input.type == "date") {
                                                        return <DateInput helperText={input.helperText ?? ""} key={`input-${ind1}`} id={input.column} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} onChange={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })} />
                                                    } else if (input.type == "select") {
                                                        return (<SelectInput helperText={input.helperText ?? ""} key={`input-${ind1}`} id={input.column} label={input.label} name={input.column} values={input.values} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} onChange={(e) => mainChange(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })} />)
                                                    } else if (input.type == "file") {
                                                        return (
                                                            <FormControl key={`input-${ind1}`} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} >
                                                                <Button variant="contained" component="label">
                                                                    Enviar Arquivo <input type="file" hidden
                                                                        onChange={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })}
                                                                        onBlur={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })}
                                                                        name={input.column}
                                                                        id={input.column}
                                                                    />
                                                                </Button>
                                                            </FormControl>
                                                        )
                                                    }
                                                })
                                            }
                                        </div>
                                    </CardContent>
                                    <CardActionArea>
                                        <CardActions>
                                        </CardActions>
                                    </CardActionArea>
                                </Card>
                            </div>
                        )
                    })
                    
                }
                <Button size="small" style={{ margin: 5 }} variant="contained" color="primary" onClick={() => {
                    if (this.props.validate === undefined) {
                        this.props.request(this.state.inputVal);
                    } else {
                        if (this.props.validate(this.state.formValidate)) {
                            //this.props.request(inputValues);
                            console.log(this.state)
                        }
                    }
                }}> Salvar</Button>
                <Button size="small" style={{ margin: 5 }} variant="contained" color="primary" onClick={() => { this.props.history.goBack(); }} > Cancelar</Button>
            </div>
        )
    }
}
const mapStateToProps = store => ({
    snackbar: store.appReducer.snackbar
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(LForms))
