import React, { Component, Fragment } from 'react'
import { View, Text, Image } from 'react-native'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';

import { Api } from '../../../providers/api'
import { setInvoices } from '../../../actions/invoiceAction'
import { setAuth } from '../../../actions/authAction'
import { setCart } from '../../../actions/cartAction'
import { setProducts } from '../../../actions/productActions'
import { postAuth, getInvoices, getApiProducts } from '../../../providers/api'
import empty from '../../../../assets/empty_box.png'

class LauncherLoading extends Component {
    async componentDidMount() {
        const response = await getApiProducts();
        await postAuth();
        this.props.setProducts(response.data);
        if (this.props.cart.length === 0 && JSON.parse(localStorage.getItem("cart")) !== null) {
            let cartItems = JSON.parse(localStorage.getItem("cart"));
            this.props.setCart(cartItems)
        }
        if (this.props.auth === undefined &&
            JSON.parse(localStorage.getItem("user")) !== null)
            this.props.setAuth(JSON.parse(localStorage.getItem("user")));

        if (this.props.invoices.length === 0) {
            const response = await getInvoices();
            this.props.setInvoices(response.data);
        }
        const client = async () => {
            let response = await Api();
            document.title = response.data.data.fantasyName
            localStorage.setItem("cliente", JSON.stringify(response.data.data));

        };
        client();
        if (JSON.parse(localStorage.getItem("cliente")) == null) {
            client();
        }
    }

    render() {
        let failed = undefined;
        const doFail = async () => {
            return await setTimeout(() => {
                failed = true
            }
                , 5000);
        }
        doFail();
        const client = localStorage.getItem("cliente") == null ? {
            logo: undefined
        } :JSON.parse(localStorage.getItem("cliente"));
        const styles = {
            backgroundColor: "#fff",
            borderRadius: 3,
            padding: 10,
            paddingTop: 0
        }
        return (
            <View style={{
                flex: 1,
                flexDirection: 'row',
                alignItems: 'center',
                justifyContent: 'center'
            }}>
                {failed === undefined, failed !== true ? (
                    <div>
                        <Image source={  "https://services.yamitec.com/"+client.logo } />
                        <CircularProgress />
                    </div>
                ) : (
                        <Text> Problema ao carregar .</Text>
                    )}

            </View>
        )
    }
}
const mapStateToProps = store => ({
    pDialog: store.productReducer.productDialog,
    cart: store.cartsReducer.cart,
    invoices: store.invoiceReducer.invoices,
    auth: store.authReducer.data
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setInvoices, setAuth, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(LauncherLoading)
