import React, { Component, Fragment, useState, useRef } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
//
import { DataGrid, RowsProp, ColDef } from '@material-ui/data-grid';
//
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
import { DEFAULT_LOCALE_TEXT } from '../../providers/langs/datagrid'
//const classes = useStyles();
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
//
import TextField from '@material-ui/core/TextField';
import Input from '@material-ui/core/Input';

import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import AppBar from '@material-ui/core/AppBar';


import HomeIcon from '@material-ui/icons/Home';
import IconButton from '@material-ui/core/IconButton';
import IndeterminateCheckBoxIcon from '@material-ui/icons/IndeterminateCheckBox';
import SearchIcon from '@material-ui/icons/Search';
import ReorderIcon from '@material-ui/icons/Reorder';

import ptBR from "date-fns/locale/pt-BR";
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker,
} from '@material-ui/pickers';

import PropTypes from 'prop-types';
import MaskedInput from 'react-text-mask';

// VALIDATORS

import { validaCpf } from '../../providers/commonMethods'
//

//const [valus, setValues] = useState(new Date('2021-02-13'));
const idNumbers = [
    'cpf', 'cnpj', 'cep'
];

let inputValues = {};
async function onChangeInputs(e, params = {}) {
    //console.log(params)
    let value = e.target.value;
    let id = e.target.id ?? e.target.name;
    if(params.validate !== undefined){
        if(!params.validate(value)){
            e.target.error = true
        }
    }
    if (idNumbers.includes(e.target.id)) {
        value = value.replace(/[^\d]+/g, '');
    }

    if (value.length == 0 || value == 'Todos') {
        inputValues[id] = '';
    } else {
        if (id == 'created_at') {
            value = `${value.toJSON().split('T')[0]}`
        }
       
        if (idNumbers.includes(id)) {
            value = value.replace(/[^\d]+/g, '');
        }
        if(params.handle !== undefined){
            let request =  await params.handle(value)
            //value = request[id] ?? '';
        }
        if(params.json !== undefined){
            inputValues[params.json][id] = value;
            //console.log(params.json);
        }else{
            inputValues[id] = value
        }
        
        console.log(inputValues)
    }
}

function onClearFilter() {
    inputValues = {};
    document.getElementById('filter-form').querySelectorAll('input').forEach((item, index) => {
        console.log({ index, item })
        item.value = '';
    });

    document.getElementById('filter-form').querySelectorAll('select').forEach((item, index) => {
        console.log({ index, item, selectedIndex: item.selectedIndex })
        item.value = "Todos";
    });
}
// MASKED INPUTS 

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
        },
    },
}));

function TextMaskCustom(props) {
    const { inputRef, ...other } = props;

    return (
        <MaskedInput
            {...other}
            ref={(ref) => {
                inputRef(ref ? ref.inputElement : null);
            }}
            mask={props.mask}
           // placeholderChar={'\u2000'}

        />
    );
}


//
const DateInput = (props) => {
    const [value, setValue] = useState(new Date('2021-02-13'));
    function handleChange(value) {
        try {
            inputValues[props.id] = `${value.toJSON().split('T')[0]}`
        } catch (err) {
            console.log(err);
        }
        setValue(value);
    }
    return (<MuiPickersUtilsProvider utils={DateFnsUtils} locale={ptBR}>
        <Grid justify="space-around" style={{ flexGrow: 0, marginTop: 18 }}>
            <KeyboardDatePicker
                margin="normal"
                id={props.id}
                label={props.label ?? 'Data'}
                format="dd/MM/yyyy"
                value={value}
                onChange={handleChange}
                onBlur={handleChange}
                KeyboardButtonProps={{
