                                    }}>
                                        {
                                            
                                            form.fields.map(input => {
                                                ifjson[input.column] = input.value ?? '';
                                                inputValues[form.json ?? input.column] = form.json == undefined ?  input.value ?? '' : ifjson ;
                                                if (input.type == "text") {
                                                    if (input.mask === undefined)
                                                        return <TextField size="small" style={{ ...classes.m5, flexGrow: input.grow ?? 0, flexBasis: input.flexBasis ?? '30%' }} id={input.column} label={input.label} 
                                                        onChange={(e) => onChangeInputs(e, {handle: input.handle ?? undefined, json: form.json ?? undefined })}
                                                         onBlur={(e) => onChangeInputs(e, {handle: input.handle ?? undefined, json: form.json ?? undefined })} />
                                                    else
                                                        return (
                                                            <FormControl style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} >
                                                                <InputLabel htmlFor={input.column}>{input.label}</InputLabel>
                                                                <Input
                                                                    size="small"
                                                                    value={inputValues[input.column]}
                                                                    onChange={(e) => onChangeInputs(e, {handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })}
                                                                    onBlur={(e) => onChangeInputs(e, {handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined  })}
                                                                    
                                                                    name={input.column}
                                                                    id={input.column}
                                                                    inputProps={{
                                                                        mask: input.mask
                                                                    }}
                                                                    inputComponent={TextMaskCustom}
                                                                />
                                                            </FormControl>)

                                                } else if (input.type == "date") {
                                                    return <DateInput id={input.column} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} onBlur={(e) => onChangeInputs(e, {handle: input.handle ?? undefined, json: form.json ?? undefined })} />
                                                } else if (input.type == "select") {
                                                    return (<SelectInput id={input.column} label={input.label} name={input.column} values={input.values} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} onBlur={(e) => onChangeInputs(e, {handle: input.handle ?? undefined, json: form.json ?? undefined })} />)
