import React from "react";
import { connect, Provider } from 'react-redux'
import { bindActionCreators } from 'redux';
import { StyleSheet, Text, View, Platform } from "react-native";
import store from '../store'
import { Switch, Router, Route, Link } from "../react-router";

import Sidebar from './components/Layout/Sidebar'
import MiniDrawer from './components/Layout/Sidebar/minidrawer'
//import './index.css';

import Home from './pages/Home';
import Login from './pages/Login';
import Searcher from './pages/Searcher';
import Invoices from './pages/Invoices';
import LauncherDialog from './components/Loading/LauncherLoading'
import FullScreenLoginDialog from './components/Layout/Dialogs/FullScreenLoginDialog'
import FullScreenCartDialog from './components/Layout/Dialogs/FullScreenCartDialog'
import Header from './components/Layout/Header'
import {themeStyle} from './components/Layout/Header/style'
import Footer from './components/Layout/Footer'
import BottonNav from './components/Layout/BottonNav'

import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Box } from '@material-ui/core';
import { Redirect } from "react-router-dom";

import { setAuth } from './actions/authAction'
// Theme
const YamiTheme = createMuiTheme(themeStyle)
  //background: 'linear-gradient(45deg, #025ea2 30%, #0086e8 90%)',
  let authData = async function(){
    return JSON.parse(localStorage.getItem("user"));
  }

const AppRouter = (props) => {
  
  const isAuth = authData !== null ? true : false;
  props.setAuth(authData);
  return props.products !== undefined ? (<LauncherDialog />) : (
  <ThemeProvider theme={YamiTheme}>
    <Header />
    <Container maxWidth="xl" style={{ overflow: 'hidden', paddingTop: 80, background: '#f1f1f1', height: window.innerHeight }}>
      <Box>
        <Router>
          <Switch>
            <Route path="/login" exact={true} component={Login} />
            <Route path="/" exact={true} render={() => (isAuth ?  <Home /> : <Redirect to="/login" />)} />
            <Route path="*">
              <Box>
                <View> Pagina não encontrada.</View>
              </Box>
            </Route>
          </Switch>
          { /*window.innerWidth < 767 &&
            <BottonNav />}
          {window.innerWidth >= 767 &&
          <Sidebar /> */}
        </ Router>
        <Footer />
      </Box>
    </Container>
  </ThemeProvider>)
//)};
}

const styles = StyleSheet.create({
  container: {
    marginTop: 25,
    padding: 10
  },
  nav: {
    flexDirection: 'row',
    justifyContent: 'space-around',
  },
});
const mapStateToProps = store => ({
  cart: store.cartsReducer.cart,
  products: store.productReducer.products,
  auth: store.authReducer.data
});
const mapDispatchToProps = dispatch =>
  bindActionCreators({ setAuth }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(AppRouter);
