import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import CardDialogLoading from '../../Loading/CardDialogLoading'
import { StyledAppBar } from '../Header/style'
import Dialog from '@material-ui/core/Dialog';
import { setSnackbar } from '../../../actions/appActions'
import { setDialog, setCart } from '../../../actions/cartAction'

import { checkImageUrl } from '../../../providers/commonMethods'
import Toolbar from '@material-ui/core/Toolbar';
import Slide from '@material-ui/core/Slide';
import Snackbar from '@material-ui/core/Snackbar';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import ImageIcon from '@material-ui/icons/Image';
import WorkIcon from '@material-ui/icons/Work';
import { FormatAlignLeftSharp } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: 84,
  },
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
  media: {
    height: 140,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function FullScreenCartDialog(props) {
  const classes = useStyles();
  let hostUrl = "https://services.yamitec.com";
  const handleClose = () => {
    props.setDialog(false);
  };

  const { cart, cDialog } = props;

  const [remove, setRemove] = useState(true);

  const closeSnack = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    props.setSnackbar({ open: false, message: "" });
  };

  const doEffect = () => {
    if (cart.length === 0 && JSON.parse(localStorage.getItem("cart")) !== null) {
      let cartItems = JSON.parse(localStorage.getItem("cart"));
      //cartItems.push(item);
      //localStorage.setItem("cart", JSON.stringify(cartItems));
      props.setCart(cartItems)
    }
  }
  //localStorage.removeItem("cart")
  const onLongPress = (item) => {

    let cartItems = JSON.parse(localStorage.getItem("cart"));
    if(remove)
      cartItems.map((ci, index) => {
        if (ci.Id === item.Id) {
          cartItems.splice(index, 1);
          props.setCart(cartItems);
          localStorage.setItem("cart", JSON.stringify(cartItems));
          if(cartItems.length === 0)
            localStorage.removeItem("cart");
          props.setSnackbar({ open: true, message: `Item "${item.Nome}" removido.` })
          setTimeout(setRemove(true), 2000)
        }
    });


  }
  return (
    <div>
      <Dialog fullScreen open={cDialog} onClose={() => handleClose()} TransitionComponent={Transition}>
        <StyledAppBar position="fixed">
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={() => handleClose()} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Meu Carrinho
            </Typography>
          </Toolbar>
        </StyledAppBar>
        { (cart).length === 0 ? (<Text style={{marginTop: 150}}>Vazio</Text>) : (
        <List className={classes.root}>
          {(cart).map((item, index) => (
            <TouchableOpacity style={styles.button} onLongPress={() => { onLongPress(item) }} >
              <ListItem>
                <ListItemAvatar>
                  <Avatar src={checkImageUrl(item.thumb) ? item.thumb : checkImageUrl(hostUrl + "/" + item.thumb) ? hostUrl + "/" + item.thumb : hostUrl + "/public/assets/images/favicon.png"} />
                </ListItemAvatar>
                <ListItemText primary={item.Nome} secondary={item.price} />
              </ListItem>
            </TouchableOpacity>
          ))}
        </List>)}
        <Snackbar
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
          open={props.snackbar.open}
          onClose={closeSnack}
          autoHideDuration={3000}
          message={props.snackbar.message}
        />
      </Dialog>
    </div>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    paddingHorizontal: 10
  },
  button: {
    alignItems: "center",
    //backgroundColor: "#DDDDDD",
    padding: 10
  },
  countContainer: {
    alignItems: "center",
    padding: 10
  }
});
const mapStateToProps = store => ({
  snackbar: store.appReducer.snackbar,
  cDialog: store.cartsReducer.cartDialog,
  cart: store.cartsReducer.cart
});
const mapDispatchToProps = dispatch =>
  bindActionCreators({ setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(FullScreenCartDialog)
