const initialState = { cartDialog: false, cart : [] }

export default (state = initialState, action) => {
    switch (action.type) {
        case 'SET_CART_DIALOG':
            return { ...state, cartDialog: action.payload }
        case 'SET_CART':
            return { ...state, cart: action.payload }
        default:
            return state
    }
}
