import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
//
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Paper from '@material-ui/core/Paper';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';

import Button from '@material-ui/core/Button';
import HomeIcon from '@material-ui/icons/Home';
import BlockIcon from '@material-ui/icons/Block';
import EditIcon from '@material-ui/icons/Edit';

import Typography from '@material-ui/core/Typography';

import Snackbar from '@material-ui/core/Snackbar';

import LForms from '../../../components/Forms';
//
import { setDialog, setProducts } from '../../../actions/productActions'
import { setCart } from '../../../actions/cartAction'
import { setSnackbar } from '../../../actions/appActions'
import { postApiContributors, getAddressByCepla } from '../../../providers/api'
import { validaCpf, stringToDate } from '../../../providers/commonMethods'

import { InputCep, InputCpf, InputPhone } from '../../../providers/masks'
//const classes = useStyles();
class CreateContributors extends Component {
    state = {
        contributors: [],
        states: []
    }
    /*async setPage(params = {page: 1} ){
        const contributors = await getApiContributors(params);
        this.setState({...this.state, contributors }) 
    } */
    async componentDidMount() {


    }

    render() {
        const closeSnack = (event, reason) => {
            if (reason === 'clickaway') {
              return;
            }
            this.props.setSnackbar({ open: false, message: "" });
          };

        const authData = JSON.parse(localStorage.getItem("user"));
        const getAddress = async (cep) => {
            let addr = await getAddressByCepla(cep);
            console.log(addr);
            if (addr !== undefined)
                document.getElementById('data-form-addr').querySelectorAll('input').forEach((item, index) => {
                    let id = item.name ?? item.id;
                    console.log(id);
                    if (id == "address") {
                        item.value = addr.data.logradouro
                    } else if (id == "state") {
                        item.value = addr.data.uf
                    } else if (id == "city") {
                        item.value = addr.data.cidade
                    }
                    console.log({ index, item })
                    //item.value = '';
                });
            if (addr !== undefined)
                document.getElementById('data-form-addr').querySelectorAll('select').forEach((item, index) => {
                    let id = item.name ?? item.id;
                    if (id == "state") {
                        item.value = addr.data.state
                    }
                });

        }
        const classes = {
            root: {
                maxWidth: 345,
                height: window.innerHeight - 150
            },
            media: {
                height: 140,
            },
        }
        const flexBasis = '30%';
        const forms = [
            {
                title: 'Dados Básicos', fields: [
                    { column: 'cpf', label: 'Cpf', type: 'text', mask: InputCpf, validate: validaCpf, flexBasis: '20%' },
                    { column: 'name', label: 'Nome', type: 'text', flexBasis },
                    {
                        column: 'function', label: 'Função', type: 'select',
                        values: [
                            "Coordenador de usuários",
                            "Coordenador de parceiros",
                            "Gerente",
                            "Operador de marketing"
                        ],
                        value: "Coordenador de usuários",
                        flexBasis
                    },
                    //{ column: 'birthdate', label: 'Data de Nascimento', type: 'date', flexBasis },
                    //{ column: 'anexo', label: 'Anexar Documento', type: 'file', flexBasis },
                    { column: 'active', label: 'Situação', type: 'select', values: ["Ativo", "Inativo"], value: "Ativo", flexBasis },
                    //{ column: 'created_at', label: 'Data', type: 'date' },
                ]
            },
            {
                id: 'addr',
                title: 'Endereço',
                flexFlow: 'row no-wrap',
                json: "address",
                fields: [
                    {
                        column: 'cep', label: 'CEP', type: 'text', mask: InputCep, flexBasis: '20%',
                        //handle: getAddress 
                    },
                    { column: 'street', label: 'Endereço', type: 'text', flexBasis },
                    { column: 'complement', label: 'Complemento', type: 'text', flexBasis: '20%' },
                    {
                        column: 'state', label: 'Endereço', type: 'select',
                        values: ["Acre", "Alagoas", "Amazonas", "Amapá", "Bahia", "Ceará", "Brasília", "Espírito Santo", "Goiás", "Maranhão", "Minas Gerais", "Mato Grosso do Sul", "Mato Grosso", "Pará", "Paraíba", "Pernambuco", "Piauí", "Paraná", "Rio de Janeiro", "Rio Grande do Norte", "Rondônia", "Roraima", "Rio Grande do Sul", "Santa Catarina", "Sergipe", "São Paulo", "Tocantins"],
                        value: "São Paulo", flexBasis, flexGrow: 2
                    },
                    { column: 'city', label: 'Cidade', type: 'text', flexBasis },
                ]
            },
            {
                title: 'Contato',
                json: 'social',
                fields: [
                    { column: 'contact1', label: 'Contato', type: 'text', mask: InputPhone, flexBasis: '33%' },
                    { column: 'contact2', label: 'Contato', type: 'text', mask: InputPhone, flexBasis: '33%' },
                    { column: 'email', label: 'E-mail', type: 'text', flexBasis: '33%' },
                ]
            },
            {
                json: 'social',
                title: 'Redes Sociais',
                fields: [
                    { column: 'linkedin', label: 'LinkedIn', type: 'text', flexBasis },
                    { column: 'facebook', label: 'Facebook', type: 'text', flexBasis },
                    { column: 'instagram', label: 'Instagram', type: 'text', flexBasis },
                ]
            }
        ]

        return (
            <Fragment>
                <AppBar position="static" style={{ padding: 10, marginTop: 10, marginBottom: 10 }}>
                    <Typography variant="h6">
                        <HomeIcon />  <span>Cadastro / Colaboradores</span>
                    </Typography>
                </AppBar>
                <LForms forms={forms}
                    request={async (data) => {
                        this.props.setSnackbar({ open: true, message: "Validando Dados, Aguarde ..." })
                        data.address = JSON.stringify(data.address)
                        data.social = JSON.stringify(data.social)
                        data.active = data.active == 1 ? 1 : 0
                        let response = await postApiContributors(data);
                        //console.log(response);
                        if (response.data.success) {
                            this.props.setSnackbar({ open: true, message: response.data.message })
                        }
                    }} />
                    <Paper style={{ marginTop: 10,padding: 15, flex: 1, flexDirection: 'column', justifyContent: 'space-around'}}>
                        <div style={{ margin: 5, height: 15}}>
                            <Typography variant="text" style={{padding: 10}}>
                                Data de Adesão:  <b>{stringToDate(authData.created_at, 'DD/MM/YYYY')}</b>&nbsp;
                            </Typography>
                            <Typography variant="text" style={{padding: 10}}>
                               Id:  <b>{authData.id}</b>
                            </Typography>
                            
                        </div>
                        <div style={{ margin: 5,  height: 15, flex: 1, justifyContent: 'space-between'}}>
                            
                            <Typography variant="text" style={{padding: 10}}>
                                Ultima alteração:  <b>{stringToDate(authData.updated_at, 'DD/MM/YYYY')}</b>
                            </Typography>
                            <Typography variant="text" style={{padding: 10}}>
                                Username:  <b>{authData.name}</b>
                            </Typography>
                        </div>
                    </Paper>
                <Snackbar
                    anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                    open={this.props.snackbar.open}
                    onClose={closeSnack}
                    autoHideDuration={3000}
                    message={this.props.snackbar.message}
                />
            </Fragment>
        )
    }
}
const mapStateToProps = store => ({
    session: store.authReducer.data,
    snackbar: store.appReducer.snackbar
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(CreateContributors)
