import React, { Component, Fragment } from 'react'
import { View, Image, StyleSheet } from 'react-native';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import { makeStyles } from '@material-ui/core/styles';
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
import { getApiProducts, postAuth } from '../../providers/api'
import { checkImageUrl } from '../../providers/commonMethods'
import FullScreenProductDialog from '../../components/Layout/Dialogs/FullScreenProductDialog'
import FullScreenTimelineDialog from '../../components/Layout/Dialogs/FullScreenTimelineDialog'

import GlobalSearchBox from '../../components/Layout/Content/Wellcome'
import GridLoading from '../../components/Loading/GridLoading'
import Slides from '../../components/Slides'
import logo from '../../../assets/empty_image.png'
/** Assets */
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

//const classes = useStyles();
class Home extends Component {
    state = {
        item: undefined,
    }

    async componentDidMount() {
      /*let products = await getApiProducts();
      console.log(products);*/
      //this.props.setProducts(products);
    }

    dialogHandler(item) {
        this.props.setDialog(true)
        this.setState({ ...this.state, item });
    }

    render() {
        let hostUrl = "https://services.yamitec.com";
        const cliente = JSON.parse(localStorage.getItem("cliente"));

        let clientSteps = cliente !== null ? JSON.parse(cliente.styles_obj)[0] : {
            slides: [
                {
                  label: 'San Francisco – Oakland Bay Bridge, United States',
                  img:
                    'https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60',
                },
                {
                  label: 'Bird',
                  img:
                    'https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60',
                },
              ]
        };
        //console.log(clientSteps);
        const styles = {
            backgroundColor: "#fff",
            borderRadius: 3,
            padding: 10,
            paddingTop: 0
        }

        const steps = [
            {
              label: 'San Francisco – Oakland Bay Bridge, United States',
              img:
                'https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60',
            },
            {
              label: 'Bird',
              img:
                'https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60',
            },
            {
              label: 'Bali, Indonesia',
              img:
                'https://images.unsplash.com/photo-1537996194471-e657df975ab4?auto=format&fit=crop&w=400&h=250&q=80',
            },
          ];

        return (
            <Fragment>
              <GlobalSearchBox fullWidth />
              <Slides steps={steps} fullWidth />
                { this.props.products.length === 0 ? (
                    <GridLoading />
                ) : (
                        <div>

                        <GridList key="glh" cellHeight={230} spacing={50} cols={window.innerWidth < 767 ? window.innerWidth < 460 ? 1 : 2 : 4} >
                            { (this.props.products).map((tile, index) => (
                                <GridListTile key={tile.thumb} onClick={() => this.dialogHandler(tile)}>
                                    <Image fullWidth style={{
                                        background: 'linear-gradient(45deg, #f9f9f9 30%, #f1f1f1 90%)',
                                        boxShadow: '0 2px 3px 2px #c9c9c9',
                                        height: '94.5%',
                                        margin: 10,
                                        marginBottom: 5,
                                        borderRadius: 2,
                                        resizeMode: 'stretch'
                                    }}
                                    source={
                                        checkImageUrl(tile.thumb) ? tile.thumb : checkImageUrl(hostUrl+"/"+tile.thumb) ? logo : logo
                                    }
                                        />
                                        <GridListTileBar
                                        style={{width: '93%', marginLeft: 10, marginBottom:3, borderBottomLeftRadius: 2, borderBottomRightRadius: 2}}
                                        title={tile.Nome}
                                        subtitle={<span>{tile.Nome}</span>}
                                        actionIcon={
                                            <IconButton aria-label={`info about ${tile.Nome}`} onClick={() => this.dialogHandler(tile)}>
                                                <InfoIcon />
                                            </IconButton>
                                        }
                                    />
                                </GridListTile>
                            ))}</GridList></div>
                    )}
                <FullScreenProductDialog item={this.state.item} />
                <FullScreenTimelineDialog />
            </Fragment>
        )
    }
}
const mapStateToProps = store => ({
    cart: store.cartsReducer.cart,
    products: store.productReducer.products
});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Home)
