import React , { useState, useEffect } from 'react';
import { StyleSheet } from 'react-native'
import { makeStyles } from '@material-ui/core/styles';
import Timeline from '@material-ui/lab/Timeline';
import TimelineItem from '@material-ui/lab/TimelineItem';
import TimelineSeparator from '@material-ui/lab/TimelineSeparator';
import TimelineConnector from '@material-ui/lab/TimelineConnector';
import TimelineContent from '@material-ui/lab/TimelineContent';
import TimelineOppositeContent from '@material-ui/lab/TimelineOppositeContent';
import TimelineDot from '@material-ui/lab/TimelineDot';
import FastfoodIcon from '@material-ui/icons/Fastfood';
import LaptopMacIcon from '@material-ui/icons/LaptopMac';
import HotelIcon from '@material-ui/icons/Hotel';
import RepeatIcon from '@material-ui/icons/Repeat';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Snackbar from '@material-ui/core/Snackbar';
///
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import CardDialogLoading from '../../Loading/CardDialogLoading'
import { StyledAppBar } from '../Header/style'
import Dialog from '@material-ui/core/Dialog';
import { setSnackbar } from '../../../actions/appActions'
import { setDialog, setTimeline } from '../../../actions/timelineAction'

import { checkImageUrl } from '../../../providers/commonMethods'
import Toolbar from '@material-ui/core/Toolbar';
import Slide from '@material-ui/core/Slide';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: 84,
  },
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
  media: {
    height: 140,
  },
  paper: {
    padding: '6px 16px',
  },
  secondaryTail: {
    backgroundColor: theme.palette.secondary.main,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function FullScreenTimelineDialog(props) {
  const classes = useStyles();
  const handleClose = () => {
    props.setDialog(false);
  };

  const { timeline, cDialog, setTimeline } = props;

  const [remove, setRemove] = useState(true);
  const [item, setItem] = useState([]);

  const closeSnack = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    props.setSnackbar({ open: false, message: "" });
  };

  const doEffect = () => {
    setTimeline({
      name: 'Produto de Teste',
      description: 'Descrição de Produto de Teste',
      price: 19.99,
      status: [
        {
          id: 1,
          icon: <FastfoodIcon />,
          title: 'Pedido Realizado',
          description: 'Aguardando aprovação do Restaurante',
          timestamp: '9:22 AM'
        }
      ]
    });

    useEffect(() => { doEffect() });
    /*if (timeline.length === 0 && JSON.parse(localStorage.getItem("cart")) !== null) {
      let cartItems = JSON.parse(localStorage.getItem("cart"));
      //cartItems.push(item);
      //localStorage.setItem("cart", JSON.stringify(cartItems));
      props.setCart(cartItems)
    }*/
  }
  //localStorage.removeItem("cart")
  return (
    <div>
      <Dialog fullScreen open={cDialog} onClose={() => handleClose()} TransitionComponent={Transition}>
        <StyledAppBar position="fixed">
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={() => handleClose()} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Detalhes do Pedido {timeline.length > 0 ? timeline.id : '' }
            </Typography>
          </Toolbar>
        </StyledAppBar>
        {timeline.length > 0 ? (
        <Timeline style={{marginTop: 84}} align="alternate">
          {timeline.status.map((k, v) => {
          <TimelineItem>
            <TimelineOppositeContent>
              <Typography variant="body2" color="textSecondary">
                {v.status.timestamp}
              </Typography>
            </TimelineOppositeContent>
            <TimelineSeparator>
              <TimelineDot>
              {v.status.icon}
              </TimelineDot>
              <TimelineConnector />
            </TimelineSeparator>
            <TimelineContent>
              <Paper elevation={3} className={classes.paper}>
                <Typography variant="h6" component="h1">
                {v.status.icon}
                </Typography>
                <Typography>{v.status.description}</Typography>
              </Paper>
            </TimelineContent>
          </TimelineItem>
          })}
          <TimelineItem>
            <TimelineOppositeContent>
              <Typography variant="body2" color="textSecondary">
                10:00 am
              </Typography>
            </TimelineOppositeContent>
            <TimelineSeparator>
              <TimelineDot color="primary">
                <LaptopMacIcon />
              </TimelineDot>
              <TimelineConnector />
            </TimelineSeparator>
            <TimelineContent>
              <Paper elevation={3} className={classes.paper}>
                <Typography variant="h6" component="h1">
                  Code
                </Typography>
                <Typography>Because it&apos;s awesome!</Typography>
              </Paper>
            </TimelineContent>
          </TimelineItem>
          <TimelineItem>
            <TimelineSeparator>
              <TimelineDot color="primary" variant="outlined">
                <HotelIcon />
              </TimelineDot>
              <TimelineConnector className={classes.secondaryTail} />
            </TimelineSeparator>
            <TimelineContent>
              <Paper elevation={3} className={classes.paper}>
                <Typography variant="h6" component="h1">
                  Sleep
                </Typography>
                <Typography>Because you need rest</Typography>
              </Paper>
            </TimelineContent>
          </TimelineItem>
          <TimelineItem>
            <TimelineSeparator>
              <TimelineDot color="secondary">
                <RepeatIcon />
              </TimelineDot>
            </TimelineSeparator>
            <TimelineContent>
              <Paper elevation={3} className={classes.paper}>
                <Typography variant="h6" component="h1">
                  Repeat
                </Typography>
                <Typography>Because this is the life you love!</Typography>
              </Paper>
            </TimelineContent>
          </TimelineItem>
        </Timeline>) : ('')}
    <Snackbar
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
          open={props.snackbar.open}
          onClose={closeSnack}
          autoHideDuration={3000}
          message={props.snackbar.message}
        />
      </Dialog>
    </div>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    paddingHorizontal: 10
  },
  button: {
    alignItems: "center",
    //backgroundColor: "#DDDDDD",
    padding: 10
  },
  countContainer: {
    alignItems: "center",
    padding: 10
  }
});
const mapStateToProps = store => ({
  snackbar: store.appReducer.snackbar,
  cDialog: store.timelineReducer.timelineDialog,
  timeline: store.timelineReducer.timeline
});
const mapDispatchToProps = dispatch =>
  bindActionCreators({ setSnackbar, setDialog, setTimeline }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(FullScreenTimelineDialog)
