                //json: 'contact',
                fields: [
                    { column: 'contract_clone', label: 'Clonar Matriz', disabled: (this.state.fields['type'] == 1 ? true : false), type: 'checkbox', validate:{depends:{label: 'Tipo', value: 2, column: 'type', text: 'Filial' }}, flexBasis: "100%" },
