import MaskedInput from 'react-text-mask';
const idNumbers = [
    'cpf', 'cnpj', 'cep'
];

let inputValues = {};
async function onChangeInputs(e, params = {}) {
    let value = e.target.value;
    let id = e.target.id ?? e.target.name;

    if (idNumbers.includes(e.target.id)) {
        value = value.replace(/[^\d]+/g, '');
    }

    if (value.length == 0 || value == 'Todos') {
        inputValues[id] = '';
    } else {
        if (id == 'created_at') {
            value = `${value.toJSON().split('T')[0]}`
        }

        if (idNumbers.includes(id)) {
            value = value.replace(/[^\d]+/g, '');
        }
        if (params.handle !== undefined) {
            let request = await params.handle(value)
            //value = request[id] ?? '';
        }

        if (params.json === undefined) {
            inputValues[id] = value
            //console.log(params.json);
        } else {
            inputValues[params.json][id] = value;

        }
        //console.log(inputValues)
    }
}

// MASKED INPUTS 

function TextMaskCustom(props) {
    const { inputRef, ...other } = props;

    return (
        <MaskedInput
            {...other}
            ref={(ref) => {
                inputRef(ref ? ref.inputElement : null);
            }}
            value={props.value}
            onChange={props.onChange}
            mask={props.mask}
            placeholderChar={'\u2000'}

        />
    );
}

//
function TextInputCustom(props) {
    const { inputRef, ...other } = props;

    const [value, setValue] = useState("");
    const [error, setError] = useState(false);

    function handleChange(e) {
        const {id} = e.target;
        let val = e.target.value;
        setValue(val);
        if(props.validate !== undefined){
            if(props.validate(val) !== false){
                setError(false);
                props.onChange(e)
            }else{
                setError(true)
            }
        }
    }
    if (props.mask === undefined)
        return (
            <TextField key={`input-${props.id}`} size="small" style={props.style}
                required={props.required ?? false}
                error={error}
                value={value}
                helperText={error ? props.helperText ?? "conteúdo inválido" : ""}
                id={props.id} label={props.label} 
                onChange={handleChange}
                onBlur={handleChange}
            />
        )
    else
        return (
            <FormControl key={`input-${props.id}`} style={props.style} >
                <InputLabel htmlFor={props.id}>{props.label}</InputLabel>
                <Input
                    required={props.required ?? false}
                    size="small"
                    //value={value}
                    error={error}
                    onChange={handleChange}
                    onBlur={handleChange}
                    name={props.name}
                    id={props.id}
                    aria-describedby="component-error-text"
                    inputProps={{
                        mask: props.mask,
                        error, 
                        onChange: handleChange,
                        value
                    }}
                    inputComponent={TextMaskCustom}
                />
                {error == true ? (
                    <FormHelperText id="component-error-text">{props.helperText ?? "conteúdo inválido"}</FormHelperText>
                ) : ('')}
            </FormControl>)
}
