const DateInput = (props) => {
    const [value, setValue] = useState(new Date('2021-02-13'));
    const [error, setError] = useState(false);
    function handleChange(value) {
        try {
            if(props.validate !== undefined){
                if(props.validate(value)){
                    setError(false);
                    inputValues[props.id] = `${value.toJSON().split('T')[0]}`
                }else{
                    setError(true)
                }
            }
            
        } catch (err) {
            console.log(err);
        }
        //props.onChange(value);
        setValue(value);
    }
    return (<MuiPickersUtilsProvider utils={DateFnsUtils} locale={ptBR}>
        <Grid justify="space-around" style={{ flexGrow: 0, marginTop: 18 }}>
            <KeyboardDatePicker
                required={props.required ?? false}
                margin="normal"
                id={props.id}
                label={props.label ?? 'Data'}
                error={error}
                helperText={props.error ? props.helperText ?? "conteúdo inválido" : ""}
                format="dd/MM/yyyy"
                value={value}
                onChange={handleChange}
                onBlur={handleChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
            />
        </Grid>
    </MuiPickersUtilsProvider>)
}
