const SelectInput = (props) => {
    const [value, setValue] = useState(props.values[0]);
    const [error, setError] = useState(false);
    function handleChange(e) {
        const {value, id} = e.target;
        if(props.validate !== undefined) {
            if(props.validate(value)){
                setError(false);
                props.onChange(e)
            }else{
                setError(true)
            }
        }
        setValue(e.target.value);
    }
    return (
        <FormControl id={props.column} style={{ ...props.style, marginTop: '25px' }}>
            <InputLabel id={props.column}>{props.label}</InputLabel>
            <Select size="small"
                labelId={props.id}
                id={props.id}
                name={props.name}
                value={value}
                error={error}
                helperText={props.error ? props.helperText ?? "conteúdo inválido" : ""}
                onChange={handleChange}
                onBlur={handleChange}
            >
                {
                    props.values.map((val, ind) => {
                        return <MenuItem key={`input-${ind}`} value={val}>{val}</MenuItem>
                    })
                }

            </Select>
        </FormControl>)
}
class LForms extends Component {
    state = {
        data: [],
        filter: ['flex']
    }
    async componentDidMount() {

    }

    render() {
        const classes = {
            m5: {
                margin: 5,
                marginTop: 25,
                //flexBasis: '30%'

            },
            root: {
                maxWidth: 345,
                marginBottom: 25
            },
            media: {
                height: 140,
            },
        }

        return (
            <div>
                {
                    this.props.forms.map((form, ind) => {
                        let ifjson = {};
                        return (
                            <div>
                                <Card className={classes.root} style={{ marginBottom: 15 }}>
                                    <CardContent>
                                        <Typography onClick={() => {
                                            let filter = this.state.filter;
                                            filter[ind] = this.state.filter[ind] == 'flex' ? 'none' : 'flex'
                                            this.setState({ ...this.state, filter })
                                        }}>
                                            <IndeterminateCheckBoxIcon /> {form.title}
                                        </Typography>

                                        <div key={`block-${ind}`} id={`data-form-${form.id ?? '1'}`} style={{
                                            alignItems: 'center',
                                            flexFlow: form.flexFlow ?? 'row wrap',
                                            justifyContent: 'start',
                                            display: this.state.filter[ind] ?? 'flex',
                                        }}>
                                            {

                                                form.fields.map((input, ind1) => {
                                                    ifjson[input.column] = input.value ?? '';
                                                    inputValues[form.json ?? input.column] = form.json == undefined ? input.value ?? '' : ifjson;
                                                    if (input.type == "text") {
                                                        return <TextInputCustom  style={{ ...classes.m5, flexGrow: input.grow ?? 0, flexBasis: input.flexBasis ?? '30%' }}
                                                        id={input.column} label={input.label}
                                                        mask={input.mask ?? undefined}
                                                        validate={input.validate}
                                                        helperText={input.helperText ?? ""}
                                                        onChange={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined })}
                                                        onBlur={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined })}/>

                                                    } else if (input.type == "date") {
                                                        return <DateInput helperText={input.helperText ?? ""} key={`input-${ind1}`} id={input.column} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} onChange={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined })} />
                                                    } else if (input.type == "select") {
                                                        return (<SelectInput helperText={input.helperText ?? ""} key={`input-${ind1}`} id={input.column} label={input.label} name={input.column} values={input.values} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} onChange={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined })} />)
                                                    } else if (input.type == "file") {
                                                        return (
                                                            <FormControl key={`input-${ind1}`} style={{ ...classes.m5, flexGrow: input.grow ?? 0 }} >
                                                                <Button variant="contained" component="label">
                                                                    Enviar Arquivo <input type="file" hidden
                                                                        onChange={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })}
                                                                        onBlur={(e) => onChangeInputs(e, { handle: input.handle ?? undefined, json: form.json ?? undefined, validate: input.validate ?? undefined })}
                                                                        name={input.column}
                                                                        id={input.column}
                                                                    />
                                                                </Button>
                                                            </FormControl>
                                                        )
                                                    }
                                                })
                                            }
                                        </div>
                                    </CardContent>
                                    <CardActionArea>
                                        <CardActions>
                                        </CardActions>
                                    </CardActionArea>
                                </Card>
                            </div>
                        )
                    })
                }
                <Button size="small" style={{ margin: 5 }} variant="contained" color="primary" onClick={() => { 
                    if(this.props.validate(inputValues)){
                        this.props.request(inputValues); 
                    }
                }}> Salvar</Button>
                <Button size="small" style={{ margin: 5 }} variant="contained" color="primary" onClick={() => { this.props.history.goBack(); }} > Cancelar</Button>

            </div>
        )
    }
}
