                        json: true, 
                        valueLabel: "name",
                        values: this.state.providertypes,//[{id: 1, value: "Farmácia"},{id: 2, value: "Loja"}],
                        validate: {required: true },
                        value: "Selecione",
                        flexBasis:'13%'
                    },
                    {
                        column: 'matriz_id', 
                        label: 'Matriz', 
                        type: 'custom',
                        json: true, 
                        valueLabel: "company_name",
                        values: this.state.providers,
                        flexBasis:'30%',
                        component: TypeEmpresaInput,
                        handler: async (id) => {
