                            let fields = this.state.fields;
                            Object.entries(matrizData.addresses)
                                .map(([key, val]) => {
                                    fields[key] = val
                            });
                            Object.entries(matrizData.contacts)
                                .map(([key, val]) => {
                                    fields[key] = val
                            });
                            Object.entries(matrizData.contracts)
                                .map(([key, val]) => {
                                    fields[key] = val
                            });
                            
