import React, { Component, Fragment, useState } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
//
import { DataGrid, RowsProp, ColDef } from '@material-ui/data-grid';
//
import { setDialog, setProducts } from '../../actions/productActions'
import { setCart } from '../../actions/cartAction'
import { setSnackbar } from '../../actions/appActions'
import { DEFAULT_LOCALE_TEXT } from '../../providers/langs/datagrid'
//const classes = useStyles();
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
//
import TextField from '@material-ui/core/TextField';

import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import ptBR from "date-fns/locale/pt-BR";
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
//
const DateInput = (props) => {
    const [value, setValue] = useState(new Date('2021-02-13'));
    function handleChange(value) {
        console.log(value.toISOString().split('T')[0])
        setValue(value);
    }
    return (<MuiPickersUtilsProvider utils={DateFnsUtils} locale={ptBR}>
                <Grid justify="space-around">
                <KeyboardDatePicker
                    margin="normal"
                    id="date-picker-dialog"
                    label={props.label ?? 'Data'}
                    format="dd/MM/yyyy"
                    value={value}
                    onChange={handleChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                    />
                </Grid>
            </MuiPickersUtilsProvider>)
}

class LDataGrid extends Component {
    state = {
        data: [],
    }
    async setPage(params = { page: 1 }) {
        const data = await this.props.pageRequest(params);
        this.setState({ ...this.state, data })
    }
    async componentDidMount() {
        this.setPage();
    }

    render() {
        const classes = {
            m5: {
                margin: 5,
            },
            root: {
                maxWidth: 345,
            },
            media: {
                height: 140,
            },
        }

        return (
            <div>
                <Card className={classes.root}>
                    <CardContent>
                    <TextField style={classes.m5} id="standard-basic" label="Standard" />
                    
                    <FormControl style={classes.m5} >
                        <InputLabel id="demo-simple-select-label">Age</InputLabel>
                        <Select
                            labelId="demo-simple-select-label"
                            id="demo-simple-select"
                            value={10}
                            onChange={() => {}}
                        >
                            <MenuItem value={10}>Ten</MenuItem>
                            <MenuItem value={20}>Twenty</MenuItem>
                            <MenuItem value={30}>Thirty</MenuItem>
                        </Select>
                    </FormControl>

                    <DateInput style={classes.m5} />
                        
                    </CardContent>
                    <CardActionArea>
                    </CardActionArea>
                    <CardActions>

                    </CardActions>
                </Card>

                <div style={{ height: window.innerHeight - 250, width: '100%' }}>
                    <DataGrid rows={this.state.data.data ?? []} columns={this.props.columns}
                        disableClickEventBubbling
                        localeText={DEFAULT_LOCALE_TEXT}
                        paginationMode="server"
                        rowCount={this.state.data.total ?? 0}
                        pageSize={10} rowsPerPageOptions={[10, 20, 50]} pagination
                        onPageChange={(params) => {
                            console.log(params);
                            this.setPage({ page: params.page, pageSize: params.pageSize });
                        }}
                    />
                </div>
            </div>
        )
    }
}
const mapStateToProps = store => ({

});
const mapDispatchToProps = dispatch =>
    bindActionCreators({ setProducts, setSnackbar, setDialog, setCart }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(LDataGrid)
